/*
* This gets any creatures that are grabbed within 5 ft of the worm and launches them at the c reature it is targetting
*
*
*/

function isGrabbed(tok) {
    return !!tok.actor.conditions.bySlug('grabbed').length
}

function getTokensInRange(selectedToken = null, extraSquares = 1) {
  // If no token is provided, use the currently selected token
  if (!selectedToken) {
    selectedToken = canvas.tokens.controlled[0];
  }

  if (!selectedToken) {
    ui.notifications.warn("No token selected or provided.");
    return null;
  }

  // Get all tokens on the canvas
  const allTokens = canvas.tokens.placeables;

  // Calculate the range based on token size and extra squares
  const tokenSize = selectedToken.document.width;
  const range = (tokenSize / 2) + extraSquares;
  console.log({tokenSize, range})

  // Array to store tokens in range
  let tokensInRange = [];

  // Iterate through all tokens
  for (let token of allTokens) {
    // Skip the selected token itself
    if (token === selectedToken) continue;
    if (!isGrabbed(token)) continue;

    // Calculate the distance between the selected token and the current token
    const distance = canvas.grid.measurePath([selectedToken.center, token.center]).spaces;
    console.log({distance, token})

    // Add the token to the array if it's within range
    if (distance <= range) {
      tokensInRange.push({ token: token, distance: distance });
    }
  }

  // Sort tokens by distance
  tokensInRange.sort((a, b) => a.distance - b.distance);

  return tokensInRange;
}

